REPLACE INTO `creature_ai_texts`(`entry`,`content_default`,`content_loc8`) VALUES ( '-308352','There is word from Jotunheim. The sleep-watchers there believe that they have found someone of significance.','');
REPLACE INTO `creature_ai_texts`(`entry`,`content_default`,`content_loc8`) VALUES ( '-308353','Look like, my lady? A vrykul, I suppose. They did not actually show him to me. Ever since The Shadow Vault....','');
REPLACE INTO `creature_ai_texts`(`entry`,`content_default`,`content_loc8`) VALUES ( '-308354','A name? Oh, yes, the name! I believe it was Iskalder.','');
REPLACE INTO `creature_ai_texts`(`entry`,`content_default`,`content_loc8`) VALUES ( '-308355','Right away, my lady.','');
REPLACE INTO `creature_ai_texts`(`entry`,`content_default`,`content_loc8`) VALUES ( '-308361','Report.','');
REPLACE INTO `creature_ai_texts`(`entry`,`content_default`,`content_loc8`) VALUES ( '-308362','Describe this vrykul. What does he look like?','');
REPLACE INTO `creature_ai_texts`(`entry`,`content_default`,`content_loc8`) VALUES ( '-308363','I am not interested in excuses. Perhaps they gave you a name?','');
REPLACE INTO `creature_ai_texts`(`entry`,`content_default`,`content_loc8`) VALUES ( '-308364','Iskalder? You fool! Have you no idea who that is? He\'s only the greatest vrykul warrior who ever lived!','');
REPLACE INTO `creature_ai_texts`(`entry`,`content_default`,`content_loc8`) VALUES ( '-308365','Return to Jotunheim and tell them to keep him asleep until I arrive. I will judge this vrykul with my own eyes.','');
