REPLACE INTO `creature_ai_texts` VALUES ('-242481', 'Shamanism has brought you here... Its scent permeates the air. *The Lich King laughs* I was once a shaman.', '', '', '', '', '', '', '', '', '0', '0', '0', '0', 'ytdb-q11343');
REPLACE INTO `creature_ai_texts` VALUES ('-242482', 'No, minion. This one is not ready.', '', '', '', '', '', '', '', '', '0', '0', '0', '0', 'ytdb-q11343');
REPLACE INTO `creature_ai_texts` VALUES ('-242483', 'Do you feel it, mortal? Death seeps through me, enveloping all that I touch. With just a snap of my finger your soul will languish in damnation for all eternity.', '', '', '', '', '', '', '', '', '0', '0', '0', '0', 'ytdb-q11343');
REPLACE INTO `creature_ai_texts` VALUES ('-242484', 'But... It is not yet your time to serve the Lich King. Yes, a greater destiny awaits you. Power... You must become more powerful before you are to serve me.', '', '', '', '', '', '', '', '', '0', '0', '0', '0', 'ytdb-q11343');
REPLACE INTO `creature_ai_texts` VALUES ('-242485', 'Now watch, val\'kyr. Observe as I apply pressure. Can you see that it is not yet ripe? Watch as it pops and falls lifeless to the floor. ', '', '', '', '', '', '', '', '', '0', '0', '0', '0', 'ytdb-q11343');
REPLACE INTO `creature_ai_texts` VALUES ('-243271', 'Shall we prepare it for you, my lord?', '', '', '', '', '', '', '', '', '0', '0', '0', '0', 'ytdb-q11343');
